import React, { useState } from 'react';
import axios from 'axios';

const Contact = () => {
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [clinic, setClinic] = useState('');
  const [specialist, setSpecialist] = useState('');
  const [preferredDate, setPreferredDate] = useState('');

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleClinicChange = (e) => {
    setClinic(e.target.value);
  };

  const handleSpecialistChange = (e) => {
    setSpecialist(e.target.value);
  };

  const handlePreferredDateChange = (e) => {
    setPreferredDate(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/api/agendamento', {
        name,
        message,
        clinic,
        specialist,
        preferredDate,
      });

      console.log('Resposta do servidor:', response.data);

      // Limpar os campos após o envio
      setName('');
      setMessage('');
      setClinic('');
      setSpecialist('');
      setPreferredDate('');
    } catch (error) {
      console.error('Erro ao enviar os dados:', error);
    }
  };

  return (
    <div>
      <h2>Agende sua consulta aqui</h2>
      <p>Entre em contato conosco!</p>

      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Nome:</label>
          <input type="text" id="name" name="name" value={name} onChange={handleNameChange} />
        </div>
        <div>
          <label htmlFor="message">Mensagem:</label>
          <textarea id="message" name="message" rows="4" value={message} onChange={handleMessageChange} />
        </div>
        <div>
          <label htmlFor="clinic">Unidade de Atendimento Desejada:</label>
          <input type="text" id="clinic" name="clinic" value={clinic} onChange={handleClinicChange} />
        </div>
        <div>
          <label htmlFor="specialist">Especialista Desejado:</label>
          <input type="text" id="specialist" name="specialist" value={specialist} onChange={handleSpecialistChange} />
        </div>
        <div>
          <label htmlFor="preferredDate">Data Preferida:</label>
          <input type="date" id="preferredDate" name="preferredDate" value={preferredDate} onChange={handlePreferredDateChange} />
        </div>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default Contact;
